package capaLogica;import capaExcepcion.BDException;import capaExcepcion.PersonaExepcion;import capaPersistencia.guardarPersona;import java.sql.SQLException;public class fachadaPersona {        public void guardarPersona (Persona pers) throws Exception, SQLException{        guardarPersona per= new guardarPersona();        per.guardarPersona(pers);    }        public Persona busquedaCI (String ci) throws PersonaExepcion, BDException, SQLException, Exception {         Persona per= new Persona();        guardarPersona pers=new guardarPersona();        per=pers.busquedaCI(ci);        return per;    }        public void eiminarPer (String ci) throws PersonaExepcion, BDException, SQLException, Exception {         Persona per= new Persona();        guardarPersona pers=new guardarPersona();        per=pers.eliminarPer(ci);            }}